import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';

const containerStyle = {
    width: '100%', // Full width of the container for better responsiveness
    height: '800px'
};

const layoutStyle = {
    display: 'flex',
    flexDirection: 'row'
};

const listStyle = {
    width: '300px', // Width of the list
    overflowY: 'auto', // Enable scrolling
    height: '800px', // Match the map height
    padding: '20px',
    boxSizing: 'border-box'
};

const Search = ({ selectedVolunteeringOptions, setSelectedVolunteeringOptions }) => {
    const [currentLocation, setCurrentLocation] = useState(null);
    const [charities, setCharities] = useState([]);
    const [map, setMap] = useState(null);

    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: "AIzaSyClSmoUtoTchbsocJmVoUSbXfwWHFCoPOo"
    });

    useEffect(() => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                position => {
                    const pos = {
                        lat: position.coords.latitude,
                        lng: position.coords.longitude
                    };
                    setCurrentLocation(pos);
                    fetchNearbyCharities(pos);
                },
                error => {
                    console.error('Geolocation failed: ', error);
                }
            );
        }
    }, []);

    const fetchNearbyCharities = useCallback((location) => {
        const exampleCharities = [
            { name: "Charity A", location: { lat: location.lat + 0.01, lng: location.lng + 0.01 } },
            { name: "Charity B", location: { lat: location.lat - 0.02, lng: location.lng - 0.02 } },
            // More charities
        ];
        const sortedCharities = exampleCharities.sort((a, b) => {
            const distA = Math.hypot(location.lat - a.location.lat, location.lng - a.location.lng);
            const distB = Math.hypot(location.lat - b.location.lat, location.lng - b.location.lng);
            return distA - distB;
        });
        setCharities(sortedCharities);
    }, []);

    const onLoad = useCallback(function callback(map) {
        setMap(map);
    }, []);

    const onUnmount = useCallback(function callback(map) {
        setMap(null);
    }, []);

    return (
        <div style={layoutStyle}>
            <div style={listStyle}>
                <h2>Charities Near You</h2>
                <ul>
                    {charities.map((charity, index) => (
                        <li key={index}>
                            <strong>{charity.name}</strong> - Approx distance: {Math.round(charity.distance * 1000)} meters
                        </li>
                    ))}
                </ul>
            </div>
            <div>
                {isLoaded && currentLocation ? (
                    <GoogleMap
                        mapContainerStyle={containerStyle}
                        center={currentLocation}
                        zoom={15.25}
                        onLoad={onLoad}
                        onUnmount={onUnmount}
                    >
                        {charities.map((charity, index) => (
                            <Marker key={index} position={charity.location} label={charity.name} />
                        ))}
                    </GoogleMap>
                ) : <div>Loading...</div>}
            </div>
        </div>
    );
}

export default Search;
