import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';

const containerStyle = {
    width: '700px',
    height: '800px'
};

const generateEvents = (center) => {
    const categories = [
        "Community Cleanup",
        "Food Bank Assistance",
        "Senior Support",
        "Youth Mentoring",
        "Animal Shelter Help"
    ];

    const events = categories.flatMap(category => ({
        name: `${category} Event`,
        category,
        location: {
            lat: center.lat + (Math.random() * 0.015 - 0.005),
            lng: center.lng + (Math.random() * 0.015 - 0.005)
        }
    }));

    return events;
};


const Search = ({ selectedVolunteeringOptions }) => {
    const [currentLocation, setCurrentLocation] = useState({ lat: 42.1037217, lng: -87.9602075 });
    const [events, setEvents] = useState([]);
    const [map, setMap] = useState(null);

    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: "AIzaSyClSmoUtoTchbsocJmVoUSbXfwWHFCoPOo"
    });

    useEffect(() => {
        const allEvents = generateEvents(currentLocation);
        const filteredEvents = allEvents.filter(event => selectedVolunteeringOptions.includes(event.category));
        const sortedEvents = filteredEvents.sort((a, b) => {
            const distA = Math.hypot(currentLocation.lat - a.location.lat, currentLocation.lng - a.location.lng);
            const distB = Math.hypot(currentLocation.lat - b.location.lat, currentLocation.lng - b.location.lng);
            return distA - distB;
        });
        setEvents(sortedEvents);
    }, [selectedVolunteeringOptions, currentLocation]); // Add currentLocation to the dependencies array


    useEffect(() => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                position => {
                    const pos = {
                        lat: position.coords.latitude,
                        lng: position.coords.longitude
                    };
                    setCurrentLocation(pos);
                },
                error => {
                    console.error('Geolocation failed: ', error);
                }
            );
        }
    }, []);

    const onLoad = useCallback(function callback(map) {
        setMap(map);
    }, []);

    const onUnmount = useCallback(function callback(map) {
        setMap(null);
    }, []);

    return (
        <div className='centerMainDiv'>
            <div className='searchMainDiv'>
                <div className='searchListStyle'>
                    <h2>Events Near You</h2>
                    <div className="event-list-container">
                        {events.map((event, index) => {
                            const distance = Math.round(Math.hypot(currentLocation.lat - event.location.lat, currentLocation.lng - event.location.lng) * 69);
                            const distanceText = `${distance} ${distance === 1 ? 'mile' : 'miles'} away`;
                            return (
                                <div key={index} className="event-card">
                                    <div className="event-name">{event.name}</div>
                                    <div className="event-details">{distanceText}</div>
                                </div>
                            );
                        })}
                    </div>
                </div>
                {isLoaded && (
                    <GoogleMap
                        mapContainerStyle={containerStyle}
                        center={currentLocation}
                        zoom={15.25} // closer zoom to show events within about a mile
                        onLoad={onLoad}
                        onUnmount={onUnmount}
                    >
                        {events.map((event, index) => (
                            <Marker key={index} position={event.location} label={event.name} />
                        ))}
                    </GoogleMap>
                )}
            </div>
        </div>
    );
}

export default Search;
