import { useNavigate } from 'react-router-dom';
import { errorExplinationMark } from './Icons';

const Home = () => {
    const navigate = useNavigate();
    const [statusUI, showStatusUI] = useState(true);
    const [fadeOut, setFadeOut] = useState(false);
    const [newNoteValue, setNewNoteValue] = useState("");
        const redirectToHomePage = () => navigate('/');


    const openUI = useCallback((type) => {
        setFadeOut(false);
        if (type === "status") showStatusUI(true)
        return
      }, []);

      const closeUI = useCallback((type) => {
        setFadeOut(true);
        if (type === "status") setTimeout(() => showStatusUI(false), 75);
        return
      }, []);

    return (
        <>
            <div className="home">
            <Modal isOpen={statusUI} style={customStyles} className={`react-modal ${fadeOut ? 'fadeOut' : ''}`} contentLabel="Status Modal" >
        <div className='statusModail'>
          <div className="socialModailText">Redeem Codes</div>
          <div className="smallSocialModailText">{version}</div>
          <div className="centeredSocialMediaDiv">
            <input className="settingsSocialStatus meherSocialButton" type="text" contentEditable="true" onChange={(e) => setNewNoteValue(e.target.value)} placeholder='Code' />
          </div>
        </div>
        <div className="bottom-right-ui-container modalBottomUi">
          <div className="bottomRightUiV3 greenBG" onClick={() => { closeUI("status"); }}>Redeem</div>
          <div className="bottomRightUiV3" onClick={() => redirectHome()}>Home</div>
        </div>
      </Modal>

            </div>
        </>
    );
};

export default Home;
