import React, { useCallback, useState, useEffect } from 'react';
import { Link, NavLink, useNavigate } from 'react-router-dom';
import Dropdown from './Dropdown';
import logo from './Icons/logo.png';
import notificationBell from './Icons/notificationBell.png';
import searchIcon from './Icons/searchIcon.png';
import './Navbar.css';
import { useNotification } from "./Notifications/NotificationProvider";
import Modal from 'react-modal';
import { customStyles } from './Modules/CustomStyle';
import { errorExplinationMark } from './Icons';

const Navbar = () => {
  const { addNotification } = useNotification();
  const [isNotificationVisible, setIsNotificationVisible] = useState(false);
  const [fadeOut, setFadeOut] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [notificationCount, setNotificationCount] = useState(0);
  const [gatheredNotifications, setGatheredNotifications] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (loggedIn && !gatheredNotifications) {
      gatherNotifications()
      setGatheredNotifications(true)
    }
    if (isNotificationVisible && loggedIn) gatherNotifications()
  }, [isNotificationVisible, gatheredNotifications, loggedIn]);


  const handleLogout = useCallback(() => {
    const { profilePicture, fullName } = akrAuth;

    logOutUser()
    navigate('/');

    return addNotification({
      title: `Signed out`,
      message: `(${fullName}) Signed out successfully!`,
      imageUrl: profilePicture,
    });

  }, [logOutUser, navigate, addNotification]);

  const handleSearchClick = () => {
    navigate('/search');
  }


  const openUI = async (type) => {
    setFadeOut(false);
    if (type === "notification") setIsNotificationVisible(true)
    return;
  }

  const closeUI = async (type) => {
    setFadeOut(true);
    if (type === "notification") setTimeout(() => setIsNotificationVisible(false), 75);
    return;
  }

  const renderNotification = async (type) => {
    gatherNotifications()
    return;
  }

  return (
    <nav className="navbar">
      <Link to="/" className="navbarLogoLink">
        <img className="navbarLogo" src={logo} alt="Akr" />
      </Link>
      <div className="navbar-links">
        {loggedIn ? (
          <div className="centeredContainer">
            <img className="notificationBellCSS" alt="" src={searchIcon} onClick={handleSearchClick} />
            <div className="notificationBellContainer">
              <img className="notificationBellCSS" alt="" src={notificationBell} onClick={() => openUI("notification")} />
              {notificationCount > 0 && <div className="notificationCount">{notificationCount}</div>}
            </div>
            <Dropdown loggedIn={loggedIn} akrAuth={akrAuth} onLogout={handleLogout} />
          </div>
        ) : (
          <NavLink to="/login" className="forceWhiteLink navbarLoginButton">Sign in</NavLink>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
