import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';

const containerStyle = {
    width: '600px',
    height: '800px'
};

const listStyle = {
    width: '300px',
    overflowY: 'auto',
    height: '800px',
    marginRight: '20px'
};

const Search = ({ selectedVolunteeringOptions }) => {
    const [currentLocation, setCurrentLocation] = useState({lat: 42.1037217, lng: -87.9602075});
    const [events, setEvents] = useState([]);
    const [map, setMap] = useState(null);

    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: "AIzaSyClSmoUtoTchbsocJmVoUSbXfwWHFCoPOo"
    });

    useEffect(() => {
        const allEvents = generateEvents(currentLocation);
        const filteredEvents = allEvents.filter(event => selectedVolunteeringOptions.includes(event.category));
        const sortedEvents = filteredEvents.sort((a, b) => {
            return Math.hypot(currentLocation.lat - a.location.lat, currentLocation.lng - a.location.lng) -
                   Math.hypot(currentLocation.lat - b.location.lat, currentLocation.lng - b.location.lng);
        });
        setEvents(sortedEvents);
    }, [selectedVolunteeringOptions]);

    const onLoad = useCallback(function callback(map) {
        setMap(map);
    }, []);

    const onUnmount = useCallback(function callback(map) {
        setMap(null);
    }, []);

    return (
        <div style={{ display: 'flex', flexDirection: 'row' }}>
            <div style={listStyle}>
                <h2>Events Near You</h2>
                <ul>
                    {events.map((event, index) => (
                        <li key={index}>
                            {event.name} ({event.category}) - Distance: {Math.round(Math.hypot(currentLocation.lat - event.location.lat, currentLocation.lng - event.location.lng) * 69)} miles
                        </li>
                    ))}
                </ul>
            </div>
            {isLoaded && (
                <GoogleMap
                    mapContainerStyle={containerStyle}
                    center={currentLocation}
                    zoom={14} // closer zoom to show events within about a mile
                    onLoad={onLoad}
                    onUnmount={onUnmount}
                >
                    {events.map((event, index) => (
                        <Marker key={index} position={event.location} label={event.name} />
                    ))}
                </GoogleMap>
            )}
        </div>
    );
}

export default Search;
