import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Cookies from 'js-cookie';
import NotFound from './NotFound';
import Modal from 'react-modal';
import './App.css'
Modal.setAppElement('#root');

function App() {
  const [akrCookie, setAkrCookie] = useState(null);
  const [akrAuth, setAkrAuth] = useState(null);
  const [loggedIn, setLoggedIn] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);


  /*
  useEffect(() => {
    document.documentElement.setAttribute("theme", theme);
  }, [theme]);
   */

  useEffect(() => {
    if (!loggedIn && akrCookie !== null && akrAuth !== null) {
      setLoggedIn(true);
      console.log(`Logged in as ${akrAuth?.fullName} (${akrAuth?.userId})`)
    }
  }, [akrCookie, akrAuth, loggedIn]);

  const setCookie = async (type, cookie) => {
    try {
      if (type === "akrCookie") {
        Cookies.set('akrCookie', cookie, { expires: 7 });
        setAkrCookie(cookie)
      }
      if (type === "akrAuth") {
        Cookies.set('akrAuth', JSON.stringify(cookie), { expires: 7 });
        setAkrAuth(cookie)
      }
    } catch (error) {
      console.error('Error:', error);
      return;
    }
  };


  return (
        <Router>
          <div>
            <Routes>
              <Route path="/" element={isLoaded ? (loggedIn ? <NotFound akrAuth={akrAuth} /> : <NotFound setCookie={setCookie} setLoggedIn={setLoggedIn} loggedIn={loggedIn} />) : <NotFound />} />
              <Route
                path="/login"
                element={<NotFound setCookie={setCookie} setLoggedIn={setLoggedIn} loggedIn={loggedIn} />}
              />

              <Route path="*" element={<NotFound />} />
            </Routes>
          </div>
        </Router>
  );
}

export default App;
