import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';

const containerStyle = {
    width: '600px', // Adjusted for better visibility
    height: '800px'
};

// Sample organizations data
const organizations = [
    { name: "North Beach Cleanup", type: "Community Cleanup", location: { lat: 42.1137217, lng: -87.9502075 } },
    { name: "Central Food Bank", type: "Food Bank Assistance", location: { lat: 42.1037217, lng: -87.9702075 } },
    { name: "Senior Center Visits", type: "Senior Support", location: { lat: 42.0937217, lng: -87.9602075 } },
    { name: "Youth Tutoring Services", type: "Youth Mentoring", location: { lat: 42.0837217, lng: -87.9502075 } },
    { name: "Animal Shelter in Park", type: "Animal Shelter Help", location: { lat: 42.0737217, lng: -87.9402075 } },
];

// Function to calculate the distance between two points in kilometers
function getDistanceFromLatLonInKm(lat1, lon1, lat2, lon2) {
    const R = 6371; // Radius of the earth in km
    const dLat = deg2rad(lat2 - lat1);  // deg2rad below
    const dLon = deg2rad(lon2 - lon1); 
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(deg2rad(lat1)) * Math.cos(deg2rad(lat2)) * 
      Math.sin(dLon/2) * Math.sin(dLon/2)
    ; 
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a)); 
    const distance = R * c; // Distance in km
    return distance;
}

function deg2rad(deg) {
    return deg * (Math.PI/180)
}

// Sort organizations by distance to a given location and then by volunteer options
function sortOrganizationsByDistanceAndType(center, organizations, selectedVolunteeringOptions) {
    const sortedByDistance = organizations.map(org => ({
        ...org,
        distance: getDistanceFromLatLonInKm(center.lat, center.lng, org.location.lat, org.location.lng)
    })).sort((a, b) => a.distance - b.distance);

    const volunteerPriority = selectedVolunteeringOptions.reduce((acc, type, index) => ({...acc, [type]: index}), {});
    return sortedByDistance.sort((a, b) => volunteerPriority[a.type] - volunteerPriority[b.type]);
}

// Assume the center location is provided like so:
const center = { lat: 42.1037217, lng: -87.9602075 };

// Sorting organizations

const Search = ({ selectedVolunteeringOptions, setSelectedVolunteeringOptions }) => {
    const [currentLocation, setCurrentLocation] = useState(center); // using the center as current location for this example
    const sortedOrganizations = sortOrganizationsByDistanceAndType(center, organizations, selectedVolunteeringOptions);
    const [map, setMap] = useState(null);
    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: "YOUR_API_KEY"
    });

    useEffect(() => {
        setCurrentLocation(center);
    }, []);

    const onLoad = useCallback(function callback(map) {
        setMap(map);
    }, []);

    const onUnmount = useCallback(function callback(map) {
        setMap(null);
    }, []);

    return isLoaded ? (
        <GoogleMap
            mapContainerStyle={containerStyle}
            center={currentLocation}
            zoom={14} // Set zoom level to approximate 1-mile distance
            onLoad={onLoad}
            onUnmount={onUnmount}
        >
            {sortedOrganizations.map((org, index) => (
                <Marker key={index} position={org.location} label={org.name} />
            ))}
        </GoogleMap>
    ) : <div>Loading...</div>;
}

export default Search;
