import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { errorExplinationMark } from './Icons';
import { GoogleMap, LoadScript, Marker, useJsApiLoader } from '@react-google-maps/api';

const containerStyle = {
  width: '400px',
  height: '400px'
};

const center = {
  lat: -3.745,
  lng: -38.523
};

const Search = ({ selectedVolunteeringOptions, setSelectedVolunteeringOptions }) => {
    const navigate = useNavigate();
    const redirectToHomePage = () => navigate('/');
    const [map, setMap] = React.useState(null)





      const onLoad = React.useCallback(function callback(map) {
        // This is just an example of getting and using the map instance!!! don't just blindly copy!
        const bounds = new window.google.maps.LatLngBounds(center);
        map.fitBounds(bounds);

        setMap(map)
      }, [])

      const onUnmount = React.useCallback(function callback(map) {
        setMap(null)
      }, [])

    return (
        <LoadScript
            googleMapsApiKey="YOUR_API_KEY">
            <div className="search-container">
                <div className="location-list">
                    {/* List of locations sorted by distance */}
                </div>
                <div className="map-container">
                    <GoogleMap
                        mapContainerStyle={containerStyle}
                        center={center}
                        zoom={10}
                    >
                        {/* Assuming you have multiple locations, you'd map over them and create a Marker for each */}
                        <Marker position={center} />
                    </GoogleMap>
                </div>
            </div>
        </LoadScript>
    );
};

export default Search;
